// WF-WD-01: Normalize Data (Simplified - No API Call Needed)

// 1. Get Webhook Data
const webhookData = $('Webhook - Blesta').item.json.body;

// 2. Get Blesta API Data (Client Profile only)
const profileList = $('Get Contact List').item.json.response;
const profile = (profileList && profileList.length > 0) ? profileList[0] : {};
const numbersList = $('Get Contact Details').item.json.response;

// 3. Robust Phone Handling
function formatPhoneForWhatsApp(phone) {
  if (!phone || phone === 'no_number_found') return null;
  phone = phone.replace(/[\s\-\(\)]/g, ''); 
  if (phone.startsWith('+27')) return phone.substring(1);
  if (phone.startsWith('27')) return phone;
  if (phone.startsWith('0')) return '27' + phone.substring(1);
  return null; 
}

// 4. Phone Number Logic
let rawPhone = "no_number_found";
if (Array.isArray(numbersList) && numbersList.length > 0) {
    const mobileObj = numbersList.find(n => n.type === 'mobile');
    rawPhone = mobileObj ? mobileObj.number : numbersList[0].number;
}
const clientPhone = formatPhoneForWhatsApp(rawPhone) || rawPhone;
const whatsappReady = formatPhoneForWhatsApp(rawPhone) !== null;

// 5. SLA & Due Date Logic (DIRECT FROM WEBHOOK)
// We grab the name directly from the webhook payload
const packageName = webhookData['_package[name]'] || 'vbronze';
const packageNameLower = packageName.toLowerCase();

let tier = 'vbronze'; // Default
if (packageNameLower.includes('platinum')) {
    tier = 'vplatinum';
} else if (packageNameLower.includes('gold')) {
    tier = 'vgold';
} else if (packageNameLower.includes('silver')) {
    tier = 'vsilver';
}

const slaMap = { 'vbronze': 48, 'vsilver': 72, 'vgold': 192, 'vplatinum': 504 };
const hoursToAdd = slaMap[tier] || 48;
const now = new Date();
const dueDate = new Date(now.getTime() + (hoursToAdd * 60 * 60 * 1000));

// 6. Unique Project ID
const projectId = `WD-${now.getFullYear()}-${Date.now().toString(36).toUpperCase()}`;

// 7. Output Construction
const projectData = {
  project_id: projectId,
  client: {
    first_name: profile.first_name || "Valued",
    last_name: profile.last_name || "Client",
    business_name: webhookData.business_name || profile.company || "Unknown Business",
    email: profile.email || "projects@mtronika.co.za",
    phone: clientPhone,
    phone_whatsapp_ready: whatsappReady,
    id: webhookData['_other[client_id]'],
    city: webhookData.city || profile.city
  },
  package: tier, // Auto-detected
  intent: {
    description: webhookData.business_intent,
    industry: webhookData.industry_sector,
    urgency: webhookData.urgency
  },
  domain: {
    name: webhookData.domain_name,
    status: webhookData.domain_status
  },
  requirements: {
    pages: webhookData.pages_needed, 
    branding: {
        colour: webhookData.brand_colour,
        logo: webhookData.logo_status
    }
  },
  meta: {
    source: "Blesta Webhook Direct",
    received_at: now.toISOString(),
    sla_hours: hoursToAdd,
    due_date: dueDate.toISOString(),
    raw_package_name: packageName 
  }
};

return { json: projectData };

