// WF-WD-01: CRITICAL DATA CHECK
// The critique says: "Don't process empty data."

const data = $('Normalize Data').item.json;
const client = data.client;

// Define "Mission Critical" fields
const missingFields = [];

// Check Name (Must not be the fallback "Valued Client")
if (!client.first_name || client.first_name === 'Valued') {
    missingFields.push('Client First Name');
}

// Check Email (Must not be the fallback default)
if (!client.email || client.email === 'projects@mtronika.co.za') {
    missingFields.push('Client Email');
}

// Check ID
if (!client.id) {
    missingFields.push('Client ID (Blesta)');
}

// THE HARD STOP
if (missingFields.length > 0) {
    // This throws an error that stops the workflow instantly
    throw new Error(`‚õî CRITICAL FAILURE: Missing Data Lineage for [${missingFields.join(', ')}]. Blesta API call may have failed.`);
}

return $input.all();

// Prepare Domain Order for Blesta
// Fire-and-forget: Blesta handles OpenSRS registration

const data = $('Normalize Data').item.json;
const domainStatus = data.domain.status; // 'existing', 'need_new', 'transfer'
const domainName = data.domain.name;
const clientId = data.client.id;

// Only create order if client needs new domain or transfer
if (domainStatus === 'need_new' || domainStatus === 'transfer') {
  return {
    json: {
      should_order: true,
      client_id: clientId,
      pricing_id: '2140', // OpenSRS .co.za pricing ID - UPDATE THIS
      domain: domainName,
      action: domainStatus === 'transfer' ? 'transfer' : 'register',
      project_id: data.project_id
    }
  };
}

// Skip if client already has domain
return { json: { should_order: false, domain: domainName, reason: 'Client has existing domain' } };
