// Build prompts for all 14 remaining pages (home already exists)
const project = $input.first().json;
const homeHtml = project.home_html || project.processed_html;

// Extract header and footer from home page for consistency
const headerMatch = homeHtml.match(/<!-- Topbar Start -->[\s\S]*?<!-- Navbar End -->/i);
const footerMatch = homeHtml.match(/<!-- Footer Start -->[\s\S]*?<!-- Footer End -->/i);
const header = headerMatch ? headerMatch[0] : '';
const footer = footerMatch ? footerMatch[0] : '';

// Extract CSS from head
const headMatch = homeHtml.match(/<head>[\s\S]*?<\/head>/i);
const headContent = headMatch ? headMatch[0] : '';

// Pages to generate (14 pages, home already done)
const pagesToGenerate = [
  { name: 'About', slug: 'about', order: 2, desc: 'Company history, mission, values, team overview' },
  { name: 'Services', slug: 'services', order: 3, desc: 'All services offered with descriptions and CTAs' },
  { name: 'Contact', slug: 'contact', order: 4, desc: 'Contact form, map, phone, email, WhatsApp, address' },
  { name: 'Gallery', slug: 'gallery', order: 5, desc: 'Portfolio/project gallery with categories and lightbox' },
  { name: 'Testimonials', slug: 'testimonials', order: 6, desc: 'Client reviews and success stories' },
  { name: 'FAQ', slug: 'faq', order: 7, desc: 'Frequently asked questions with accordion' },
  { name: 'Booking', slug: 'booking', order: 8, desc: 'Appointment/service booking form with calendar' },
  { name: 'Payment', slug: 'payment', order: 9, desc: 'Online payment page with Yoco/PayFast integration placeholder' },
  { name: 'Registration', slug: 'register', order: 10, desc: 'Client registration/signup form' },
  { name: 'Confirmation', slug: 'confirmation', order: 11, desc: 'Order/booking confirmation page' },
  { name: 'Invoice', slug: 'invoice', order: 12, desc: 'Invoice display page' },
  { name: 'Locations', slug: 'locations', order: 13, desc: 'Multi-location page with maps and branch details' },
  { name: 'Blog', slug: 'blog', order: 14, desc: 'Blog/news listing with article previews' },
  { name: 'Team', slug: 'team', order: 15, desc: 'Full team member profiles with roles and social links' }
];

return {
  json: {
    project,
    header,
    footer,
    headContent,
    pagesToGenerate,
    brandColour: project.brand_colour || '#FD5D14',
    clientName: project.client_name,
    industrySector: project.industry_sector,
    businessIntent: project.business_intent,
    phone: project.client_phone,
    email: project.client_email
  }
};

// Parse AI response and extract HTML
const aiResponse = $input.first().json;
const pageData = $('Prompt - About Page').item.json.pageData;
const project = $('Prompt - About Page').item.json.project;

let html = '';
try {
  if (aiResponse.output) {
    const htmlMatch = aiResponse.output.match(/```html([\s\S]*?)```/);
    html = htmlMatch ? htmlMatch[1].trim() : aiResponse.output;
  } else if (aiResponse.text) {
    const htmlMatch = aiResponse.text.match(/```html([\s\S]*?)```/);
    html = htmlMatch ? htmlMatch[1].trim() : aiResponse.text;
  }
} catch (e) {
  html = '<!-- Error generating page -->';
}

return {
  json: {
    project_ref: project.project_ref,
    page_name: pageData.name,
    page_slug: pageData.slug,
    page_order: pageData.order,
    page_html: html,
    is_generated: true
  }
};

